angular.module("courseAnimations",["ngAnimate"]).animation(".view-slide-in",function(){return{enter:function(element,done){element.css({opacity:.5,position:"relative",top:"10px",left:"20px"}).animate({top:0,left:0,opacity:1},1e3,done)}}}).animation(".repeat-animation",function(){return{enter:function(element,done){console.log("entering");element.width();element.css({position:"relative",left:-10,opacity:0}),element.animate({left:0,opacity:1},done)},leave:function(element,done){element.css({position:"relative",left:0,opacity:1}),element.animate({left:-10,opacity:0},done)},move:function(element,done){element.css({left:"2px",opacity:.5}),element.animate({left:"0px",opacity:1},done)}}}).animation(".hide-animation",function(){return{beforeAddClass:function(element,className,done){"ng-hide"==className?element.animate({opacity:0},500,done):done()},removeClass:function(element,className,done){"ng-hide"==className?(element.css("opacity",0),element.animate({opacity:1},500,done)):done()}}}).animation(".js-anim",function(){return{beforeAddClass:function(element,className,done){"switching"==className?element.animate({opacity:0},1e3,function(){element.css({opacity:1}),done()}):done()}}});